mapper.select:
SELECT 

FROM car a

WHERE a.id = #{id}

SELECT 

FROM car a


	a.del_flag = #{DEL_FLAG_NORMAL} and a.user_id = ${user.id} 

		AND a.car_no LIKE 
			'%'||#{carNo}||'%'
			'%'+#{carNo}+'%'
			concat('%',#{carNo},'%')


		AND a.status = #{status}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM car a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE car SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
