mapper.select:
SELECT 

FROM home a

WHERE a.id = #{id}

SELECT 

FROM home a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM home a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE home SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
