mapper.select:
SELECT 

FROM person a

WHERE a.id = #{id}

SELECT 

FROM person a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.home_id = #{home.id}




		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

SELECT 

FROM person a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

mapper.update:
DELETE FROM person


		WHERE id = #{id}


		WHERE home_id = #{home.id}

UPDATE person SET 
	del_flag = #{DEL_FLAG_DELETE}


		WHERE id = #{id}


		WHERE home_id = #{home.id}
